class Map
{
    private:
        int rows;
        int columns;
        int tile_size;
        const char *file_name;
        int number_of_tiles;


        // Integer positions of the tiles
        Matrix<Tile> tile_map;
        // Coordinates of the tiles

        sf::Texture tileset;
        vector<sf::Sprite> tile_sprites;

        sf::RenderWindow *window;
        void init_map(int tile_size, vector<vector<int>> *tile_map);
        void init_sprites(int tile_size);
    public:
        void set_number_of_tiles(int number_of_tiles) { this->number_of_tiles = number_of_tiles; }
        int get_number_of_tiles() { return number_of_tiles; }

        void set_rows(int rows) { this->rows = rows; }
        int get_rows() { return rows; }

        void set_columns(int columns) { this->columns = columns; }
        int get_columns() { return columns; }

        void set_tile_size(int tile_size) {
            this->tile_size = tile_size;
        }
        int get_tile_size() { return tile_size; }
        
        sf::Texture get_tile_set() { return tileset; }

        Map();
        Map(sf::RenderWindow *window, int rows, int cols, sf::Texture tileset, int tile_size,
                            vector<vector<int>> *tile_map, const char *file_name, int number_of_tiles);

        Matrix<Tile>* get_tile_map() { return &tile_map; }
        void draw();
};

Map::Map()
{

}

Map::Map(sf::RenderWindow *window, int rows, int cols, sf::Texture tileset, int tile_size,
                            vector<vector<int>> *tile_map, const char *file_name, int number_of_tiles)
{

    this->window = window;
    
    set_rows(rows);
    set_columns(cols);

    set_tile_size(tile_size);
    set_number_of_tiles(number_of_tiles);



    this->tileset = tileset;
    this->tile_size = tile_size;




    this->tile_map = Matrix<Tile>(rows, vector<Tile>(columns));
    this->file_name = file_name;

    init_map(tile_size, tile_map);
}

// Collect sprites from the sprite sheet
void Map::init_sprites(int tile_size)
{
    for (int x = 0; x < get_number_of_tiles(); ++x)
    {
        try
        {
            sf::Sprite sprite(tileset);
            sf::IntRect tile_rect(x * tile_size, 0, tile_size, tile_size);
            sprite.setTextureRect(tile_rect);
            tile_sprites.push_back(sprite);
        }
        catch (const std::exception& e)
        {
            std::cerr << "init_map(): sprite allocation error" << std::endl;
        }
    }
}

// Sets up the tilemap
void Map::init_map(int tile_size, vector<vector<int>> *tile_map)
{
    set_tile_size(tile_size);
    int x, y;

    // Collect sprites from the sprite sheet
    init_sprites(tile_size);

    for (x = 0; x < rows; ++x)
    {
        for (y = 0; y < columns; ++y)
        {
            /* Intializing the tile map. */
            try
            {
                /* code */
                Tile *curr_tile = &this->tile_map.at(x).at(y);
                curr_tile->set_value(tile_map->at(x).at(y));
                curr_tile->set_x(y * tile_size);
                curr_tile->set_y(x * tile_size);

                if (curr_tile->get_value() != 0)
                {
                    curr_tile->get_sprite()->setPosition(curr_tile->get_x(),curr_tile->get_y());
                    curr_tile->get_sprite()->setTexture(tileset);
                    sf::IntRect tile_rect(curr_tile->get_value() * tile_size, 0, tile_size, tile_size);
                    curr_tile->get_sprite()->setTextureRect(tile_rect);
                }
            }
            catch (const std::exception& e)
            {
                std::cerr << "init_map(): couldn't initialize all values." << '\n';
                exit(1);
            }
        }
    }


    for (x = 0; x < rows; ++x)
    {
        for (y = 0; y < columns; ++y)
        {

            Tile *which_block = &this->tile_map.at(x).at(y);
            if (which_block->get_value() != 0)
            {
                // Sets the sprite if the value isn't 0.
                which_block->set_sprite(tile_sprites.at(which_block->get_value()));
                which_block->set_x(y * tile_size);
                which_block->set_y(x * tile_size);
                which_block->get_sprite()->setPosition(
                    which_block->get_x(), which_block->get_y()
                );
            }
        }
    }
}



void Map::draw()
{
    for (int x = 0; x < rows; ++x)
    {
        for (int y = 0; y < columns; ++y)
        {
            Tile *which_block = &tile_map.at(x).at(y);
            if (which_block->get_value() != 0)
            {
                window->draw(*which_block->get_sprite());
            }
        }
    }
}

